import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Header from './Header';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for marker icon not displaying
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const parkingLots = [
  {
    id: 1,
    name: 'Perry Street Garage',
    position: [37.2312, -80.4263],
    availableSpots: 45,
    totalSpots: 120,
    status: 'Some Open'
  },
  {
    id: 2,
    name: 'Cassell Lot',
    position: [37.2214, -80.4205],
    availableSpots: 0,
    totalSpots: 80,
    status: 'Full'
  },
  {
    id: 3,
    name: 'Litton Reaves',
    position: [37.2220, -80.4267],
    availableSpots: 35,
    totalSpots: 60,
    status: 'Available'
  },
  {
    id: 4,
    name: 'Squires',
    position: [37.2291, -80.4168],
    availableSpots: 5,
    totalSpots: 40,
    status: 'Some Open'
  },
  {
    id: 5,
    name: 'Architecture Annex',
    position: [37.2283, -80.4158],
    availableSpots: 20,
    totalSpots: 30,
    status: 'Available'
  },
];

const ParkingLots = () => {
  const [selectedLot, setSelectedLot] = useState(null);

  const handleLotSelect = (lot) => {
    setSelectedLot(lot);
  };

  return (
    <div style={styles.container}>
      <Header />
      <main style={styles.main}>
        <div style={styles.contentContainer}>
          <div style={styles.listContainer}>
            <h2 style={styles.sectionTitle}>List of parking lots</h2>
            <ul style={styles.lotsList}>
              {parkingLots.map((lot) => (
                <li 
                  key={lot.id} 
                  style={{
                    ...styles.lotItem,
                    ...(selectedLot && selectedLot.id === lot.id ? styles.selectedLot : {})
                  }}
                  onClick={() => handleLotSelect(lot)}
                >
                  <span style={styles.lotName}>{lot.name}</span>
                  <span 
                    style={{
                      ...styles.lotStatus,
                      backgroundColor: lot.status === 'Full' ? 'red' : 
                                      lot.status === 'Some Open' ? 'yellow' : 'green',
                      color: lot.status === 'Some Open' ? 'black' : 'white'
                    }}
                  >
                    {lot.status}
                  </span>
                </li>
              ))}
            </ul>
          </div>

          <div style={styles.detailContainer}>
            {selectedLot ? (
              <div>
                <h2 style={styles.detailTitle}>{selectedLot.name}</h2>
                <div style={styles.lotDetails}>
                  <p>Available Spots: {selectedLot.availableSpots}/{selectedLot.totalSpots}</p>
                  <p>Status: {selectedLot.status}</p>
                  <Link 
                    to={`/parking-lots/${selectedLot.id}`} 
                    style={styles.viewDetailsButton}
                  >
                    View Floor Plan
                  </Link>
                </div>
                <div style={styles.mapWrapper}>
                  <MapContainer 
                    center={selectedLot.position} 
                    zoom={17} 
                    style={styles.map}
                  >
                    <TileLayer
                      attribution='&copy; <a href="https://osm.org/copyright">OpenStreetMap</a> contributors'
                      url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    <Marker position={selectedLot.position}>
                      <Popup>{selectedLot.name}</Popup>
                    </Marker>
                  </MapContainer>
                </div>
              </div>
            ) : (
              <div style={styles.noSelectionMessage}>
                <p>Select a parking lot to see details and floor plan</p>
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
};

const styles = {
  container: {
    backgroundColor: '#800000', // Maroon background
    minHeight: '100vh',
    margin: 0,
    padding: 0,
    color: '#FFFFFF',
    fontFamily: 'Arial, sans-serif',
  },
  main: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '20px',
  },
  contentContainer: {
    display: 'flex',
    width: '90%',
    gap: '20px',
  },
  listContainer: {
    flex: 1,
    backgroundColor: '#EEEEEE',
    borderRadius: '4px',
    padding: '20px',
    color: '#333333',
  },
  sectionTitle: {
    textAlign: 'center',
    marginTop: 0,
  },
  lotsList: {
    listStyle: 'none',
    padding: 0,
  },
  lotItem: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '10px 15px',
    backgroundColor: '#FFFFFF',
    borderRadius: '4px',
    marginBottom: '10px',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    ':hover': {
      backgroundColor: '#F0F0F0',
    },
  },
  selectedLot: {
    backgroundColor: '#E0E0E0',
    borderLeft: '5px solid #800000',
  },
  lotName: {
    fontWeight: 'bold',
  },
  lotStatus: {
    padding: '4px 8px',
    borderRadius: '4px',
    fontSize: '0.8rem',
    fontWeight: 'bold',
  },
  detailContainer: {
    flex: 2,
    backgroundColor: '#EEEEEE',
    borderRadius: '4px',
    padding: '20px',
    color: '#333333',
    minHeight: '500px',
  },
  detailTitle: {
    textAlign: 'center',
    marginTop: 0,
  },
  lotDetails: {
    backgroundColor: '#FFFFFF',
    padding: '15px',
    borderRadius: '4px',
    marginBottom: '20px',
  },
  viewDetailsButton: {
    display: 'inline-block',
    padding: '10px 15px',
    backgroundColor: '#800000',
    color: '#FFFFFF',
    textDecoration: 'none',
    borderRadius: '4px',
    textAlign: 'center',
    fontWeight: 'bold',
  },
  mapWrapper: {
    width: '100%',
    height: '300px',
    borderRadius: '4px',
    overflow: 'hidden',
  },
  map: {
    height: '100%',
    width: '100%',
  },
  noSelectionMessage: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100%',
    textAlign: 'center',
    color: '#666666',
    fontStyle: 'italic',
  },
};

export default ParkingLots;
