import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';

const Header = () => {
  const location = useLocation();

  // Determine which nav link is active
  const isActive = (path) => {
    if (path === '/' && location.pathname === '/') {
      return true;
    }
    if (path !== '/' && location.pathname.startsWith(path)) {
      return true;
    }
    return false;
  };

  // Check if user is logged in
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');

  useEffect(() => {
    const userStr = localStorage.getItem('user');
    if (userStr) {
      const userData = JSON.parse(userStr);
      setIsLoggedIn(true);
      setUsername(userData.username || '');
    } else {
      setIsLoggedIn(false);
      setUsername('');
    }
  }, [location.pathname]); // Re-check when path changes

  return (
    <header>
      <div style={styles.headerContainer}>
        <h1 style={styles.title}>VT Parking Finder</h1>
      </div>
      <nav style={styles.navBar}>
        <Link 
          to="/" 
          style={{
            ...styles.navLink,
            ...(isActive('/') ? styles.activeNavLink : {})
          }}
        >
          Home
        </Link>
        <Link 
          to="/parking-lots" 
          style={{
            ...styles.navLink,
            ...(isActive('/parking-lots') ? styles.activeNavLink : {})
          }}
        >
          Parking Lots
        </Link>
        {isLoggedIn ? (
          <Link 
            to="/profile" 
            style={{
              ...styles.navLink,
              ...(isActive('/profile') ? styles.activeNavLink : {})
            }}
          >
            {username || 'Profile'}
          </Link>
        ) : (
          <Link 
            to="/login" 
            style={{
              ...styles.navLink,
              ...(isActive('/login') ? styles.activeNavLink : {})
            }}
          >
            Login
          </Link>
        )}
      </nav>
    </header>
  );
};

const styles = {
  headerContainer: {
    width: '100%',
    backgroundColor: '#600000', // Main header background
    padding: '20px 0',
    textAlign: 'center',
  },
  title: {
    margin: 0,
    fontSize: '2rem',
    color: '#FFFFFF',
    fontWeight: 'bold',
  },
  navBar: {
    width: '100%',
    backgroundColor: '#700000', // Navigation background
    display: 'flex',
    justifyContent: 'center',
    padding: '15px 0',
  },
  navLink: {
    color: '#FFFFFF',
    textDecoration: 'none',
    margin: '0 15px',
    fontSize: '1.1rem',
    fontWeight: 'bold',
    padding: '8px 16px',
    borderRadius: '4px',
    transition: 'background-color 0.3s',
  },
  activeNavLink: {
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
  },
};

export default Header;
