/**
 * MapWithUpdatingCenter.jsx
 * 
 * Enhanced OpenStreetMap implementation with in-browser navigation features.
 * This component maintains the same API but adds routing capabilities.
 * 
 * Features:
 * - Displays an interactive map with custom markers
 * - Centers on selected parking lot with smooth animations
 * - Detects user's location for navigation
 * - Shows directions from user's current location to parking lots
 * - Calculates distance and estimated travel time
 * 
 * Changelog:
 * v1.1.0 (Current)
 * - Added user location detection functionality
 * - Implemented routing with Leaflet Routing Machine
 * - Added "Navigate Here" button to marker popups
 * - Enhanced error handling for location services
 * - Improved marker and route styling
 * - Added loading indicators during route calculation
 * 
 * v1.0.0 (Original)
 * - Basic OpenStreetMap implementation with Leaflet
 * - Simple marker display without directions
 * - Basic center adjustment functionality
 * 
 * @author VT Parking Finder Team
 * @version 1.5.0
 */

import React, { useEffect, useState, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-routing-machine/dist/leaflet-routing-machine.css';
import 'leaflet-routing-machine';

// Fix for marker icon not displaying correctly in Leaflet with React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

/**
 * Creates a custom marker icon based on the parking lot status
 * @param {string} status - The status of the parking lot ('Full', 'Some Open', or 'Available')
 * @returns {L.divIcon} A Leaflet divIcon with appropriate styling
 */
const createMarkerIcon = (status) => {
  // Determine color based on parking status
  const color = status === 'Full' ? 'red' : 
               status === 'Some Open' ? 'yellow' : 'green';

  // Create a custom div icon with the specified color
  return L.divIcon({
    className: 'custom-marker',
    html: `<div style="background-color: ${color}; width: 24px; height: 24px; border-radius: 50%; border: 2px solid white;"></div>`,
    iconSize: [25, 25],
    iconAnchor: [12, 12],
  });
};

/**
 * Custom user location marker icon
 */
const userLocationIcon = L.divIcon({
  className: 'user-location-marker',
  html: `<div style="background-color: #2A93EE; width: 16px; height: 16px; border-radius: 50%; border: 2px solid white; box-shadow: 0 0 0 5px rgba(42, 147, 238, 0.3);"></div>`,
  iconSize: [20, 20],
  iconAnchor: [10, 10],
});

/**
 * A component that handles map center updates and routing
 * 
 * @param {Object} props - Component props
 * @param {Array} props.center - The [lat, lng] coordinates to center the map on
 * @param {number} props.zoom - The zoom level to apply
 * @param {Array} props.userLocation - The user's location as [lat, lng]
 * @param {boolean} props.showRoute - Whether to show the route to the center
 * @returns {null} - This component doesn't render anything visible
 */
const MapController = ({ center, zoom, userLocation, showRoute }) => {
  const map = useMap();
  const routingControlRef = useRef(null);

  // Effect to handle map centering
  useEffect(() => {
    if (center) {
      map.flyTo(center, zoom, {
        animate: true,
        duration: 1.5 // Duration of animation in seconds
      });
    }
  }, [center, zoom, map]);

  // Effect to handle routing
  useEffect(() => {
    // Clear existing route if present
    if (routingControlRef.current) {
      map.removeControl(routingControlRef.current);
      routingControlRef.current = null;
    }

    // Create new route if userLocation and center are available and showRoute is true
    if (userLocation && center && showRoute) {
      const waypoints = [
        L.latLng(userLocation[0], userLocation[1]),
        L.latLng(center[0], center[1])
      ];

      routingControlRef.current = L.Routing.control({
        waypoints,
        routeWhileDragging: false,
        showAlternatives: false,
        fitSelectedRoutes: false,
        show: false, // Don't show the text instructions
        lineOptions: {
          styles: [
            { color: '#800000', opacity: 0.8, weight: 6 }, // VT Maroon
            { color: '#FFF', opacity: 0.3, weight: 4 }     // White outline
          ],
          extendToWaypoints: true,
          missingRouteTolerance: 0
        },
        altLineOptions: {
          styles: [
            { color: '#FF6600', opacity: 0.8, weight: 6 }, // VT Orange
            { color: '#FFF', opacity: 0.3, weight: 4 }     // White outline
          ]
        },
        createMarker: () => null // Don't create default markers
      }).addTo(map);

      // Adjust map view to show the entire route
      routingControlRef.current.on('routesfound', (e) => {
        const routes = e.routes;
        const summary = routes[0].summary;
        console.log(`Distance: ${summary.totalDistance/1000} km, Time: ${Math.round(summary.totalTime/60)} minutes`);
      });
    }

    return () => {
      if (routingControlRef.current) {
        map.removeControl(routingControlRef.current);
      }
    };
  }, [map, userLocation, center, showRoute]);

  return null;
};

/**
 * Main map component with enhanced navigation features
 * 
 * @param {Object} props - Component props
 * @param {Array} props.center - The [lat, lng] coordinates to center the map on
 * @param {number} props.zoom - The zoom level to apply
 * @param {Array} props.markers - An array of marker objects to display on the map
 * @param {Function} props.onMarkerClick - Callback function when a marker is clicked
 * @returns {JSX.Element} The rendered map component
 */
const MapWithUpdatingCenter = ({ center, zoom, markers, onMarkerClick }) => {
  const [userLocation, setUserLocation] = useState(null);
  const [selectedMarker, setSelectedMarker] = useState(null);
  const [showRoute, setShowRoute] = useState(false);
  const [isLocating, setIsLocating] = useState(false);
  const [error, setError] = useState(null);

  // Detect user's location
  const getUserLocation = () => {
    if (!navigator.geolocation) {
      setError('Geolocation is not supported by your browser');
      return;
    }

    setIsLocating(true);
    setError(null);

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        setUserLocation([latitude, longitude]);
        setIsLocating(false);
      },
      (error) => {
        console.error('Error getting user location:', error);
        setError(`Unable to get your location: ${error.message}`);
        setIsLocating(false);
      },
      {
        enableHighAccuracy: true,
        timeout: 5000,
        maximumAge: 0
      }
    );
  };

  // Get user location on component mount
  useEffect(() => {
    getUserLocation();
  }, []);

  // Handle navigation request
  const handleNavigate = (marker) => {
    if (!userLocation) {
      getUserLocation();
      setSelectedMarker(marker);
    } else {
      setSelectedMarker(marker);
      setShowRoute(true);
    }
  };

  return (
    <div style={{ position: 'relative', height: '100%', width: '100%' }}>
      <MapContainer 
        center={center || [37.225, -80.423]} // Default to VT campus
        zoom={zoom || 14.6}
        style={{ height: '100%', width: '100%', borderRadius: '4px' }}
      >
        {/* Base map tile layer - uses OpenStreetMap */}
        <TileLayer
          attribution='&copy; <a href="https://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {/* User location marker */}
        {userLocation && (
          <Marker position={userLocation} icon={userLocationIcon}>
            <Popup>
              <div>
                <strong>Your Location</strong>
              </div>
            </Popup>
          </Marker>
        )}

        {/* Parking lot markers */}
        {markers.map((marker) => (
          <Marker
            key={marker.id}
            position={marker.position}
            icon={marker.status ? createMarkerIcon(marker.status) : undefined}
            eventHandlers={{
              click: () => {
                if (onMarkerClick) onMarkerClick(marker.id);
                setSelectedMarker(marker);
              }
            }}
          >
            <Popup>
              <div style={styles.popup}>
                <h3 style={styles.popupTitle}>{marker.name}</h3>
                {/* Only show availability if the data exists */}
                {marker.availableSpots !== undefined && (
                  <p style={styles.popupInfo}>
                    Available: {marker.availableSpots}/{marker.totalSpots}
                  </p>
                )}
                {/* Only show status if the data exists */}
                {marker.status && (
                  <p style={styles.popupStatus}>
                    Status: <span style={{
                      color: marker.status === 'Full' ? 'red' : 
                             marker.status === 'Some Open' ? 'darkgoldenrod' : 'green',
                      fontWeight: 'bold'
                    }}>
                      {marker.status}
                    </span>
                  </p>
                )}
                <div style={styles.popupButtons}>
                  <button 
                    onClick={() => onMarkerClick && onMarkerClick(marker.id)} 
                    style={styles.popupButton}
                  >
                    View Details
                  </button>
                  <button 
                    onClick={() => handleNavigate(marker)} 
                    style={styles.navigateButton}
                    disabled={isLocating}
                  >
                    {isLocating ? 'Getting Location...' : 'Navigate Here'}
                  </button>
                </div>
              </div>
            </Popup>
          </Marker>
        ))}

        {/* Map controller for centering and routing */}
        <MapController 
          center={selectedMarker ? selectedMarker.position : center}
          zoom={selectedMarker ? 15 : zoom}
          userLocation={userLocation}
          showRoute={showRoute && !!selectedMarker}
        />
      </MapContainer>

      {/* Error message overlay */}
      {error && (
        <div style={styles.errorOverlay}>
          <div style={styles.errorContent}>
            <h3 style={styles.errorTitle}>Location Error</h3>
            <p>{error}</p>
            <button 
              onClick={() => setError(null)} 
              style={styles.errorButton}
            >
              Dismiss
            </button>
            <button 
              onClick={() => getUserLocation()} 
              style={styles.retryButton}
            >
              Retry
            </button>
          </div>
        </div>
      )}

      {/* Loading indicator */}
      {isLocating && (
        <div style={styles.loadingOverlay}>
          <div style={styles.loadingSpinner}></div>
          <p style={styles.loadingText}>Getting your location...</p>
        </div>
      )}

      {/* Route info panel */}
      {showRoute && selectedMarker && (
        <div style={styles.routeInfoPanel}>
          <div style={styles.routeInfoHeader}>
            <h3 style={styles.routeInfoTitle}>
              Directions to {selectedMarker.name}
            </h3>
            <button 
              onClick={() => setShowRoute(false)} 
              style={styles.closeButton}
            >
              ×
            </button>
          </div>
          <div style={styles.routeInfoContent}>
            <p>Follow the highlighted route from your current location to the parking lot.</p>
          </div>
        </div>
      )}
    </div>
  );
};

